mapper.select:
select author_id authorId, author_name authorName, sex, dynasty, description, update_time updateTime, from author
author_name like concat('%', #{keyword}, '%')
limit ${size * (page - 1)}, #{size}

mapper.update:
update author
set author_name = #{authorName}, sex = #{sex}, dynasty = #{dynasty}, description = #{description}, update_time = CURTIME()
where author_id = #{authorId}
